mapper.select:
select name as principal
from sys_user
where account = #{account}

select id,department_trainmeg as depTrainMeg, conType, principal, account from trainmeg_keep
and department_trainmeg like #{department}"%" and department_trainmeg like "%"#{name}"%" and principal like CONCAT('%', #{principal}, '%') and conType = #{conType}
limit #{page},#{limit}

mapper.update:
update trainmeg_keep
department_trainmeg = #{depTrainMeg}, conType = #{conType},
where id = #{id}
